import React, {Component} from 'react';

class EventHandlingDemo extends Component {
    clickHandler = () => {
        alert("Welcome");
    }

    render(){
        return(
            <div>
                <input type="text"></input>
                <button onClick={this.clickHandler}>CLICK ME</button>
            </div>
        )
    }
}

export default EventHandlingDemo;
