import React, {Component} from 'react'

class EventHandlingDemo extends Component {

    clickHandler = () => {
        alert("Welcome")
    }

    render(){
        return(
            <div>
                <button onClick={clickHandler()}>CLICK ME</button>
            </div>
        )
    }
}

export default EventHandlingDemo;
